contract Initializable {
    bool public initialized;

    constructor(bool testingDeployment) public {
        if (!testingDeployment) {
            initialized = true;
        }
    }

    modifier initializer() {
        require(!initialized, "contract already initialized");
        initialized = true;
        _;
    }
}
